import groovy.xml.XmlUtil

def groupID = context.expand( '${Login Details#groupID}' )
def logfile = new File('////tellusdevb05.pc.factset.com//Output//DataFeed//Snapshot_LogFiles//' + groupID + '//logfile.txt')

def tpcTicker = context.expand( '${685510-TPC-Checking data with the tickers#id}' )
def cpcTicker = context.expand( '${685511-CPC-Checking data with the tickers#id}' )

def tpc = context.expand( '${685510-TPC-Checking data with the tickers#ResponseAsXml#//Response[1]/Records[1]}' )
def cpc = context.expand( '${685511-CPC-Checking data with the tickers#ResponseAsXml#//Response[1]/Records[1]}' )

def tpcXml = new XmlSlurper().parseText(tpc)
def cpcXml = new XmlSlurper().parseText(cpc)

def tpcFieldsXml = tpcXml.Record.Fields
def cpcFieldsXml = cpcXml.Record.Fields

def tpcFieldID,cpcFieldID
def tpcFieldname,cpcFieldname,tpcFieldvalue,cpcFieldvalue

def flag = 0,temp = 0

//log.info tpcFieldsXml.Field.size()
//log.info cpcFieldsXml.Field.size()

if(tpcFieldsXml.Field.size() > 0)
{
	if(cpcFieldsXml.Field.size() > 0)
	{	
		for(i=0; i<tpcFieldsXml.Field.size();i++)
		{
			tpcFieldID = tpcFieldsXml.Field[i].@id
			tpcFieldname = tpcFieldsXml.Field[i].@name
			tpcFieldvalue = tpcFieldsXml.Field[i].@value
			for(j=0;j<cpcFieldsXml.Field.size();j++)
			{
				cpcFieldID = cpcFieldsXml.Field[j].@id
				cpcFieldname = cpcFieldsXml.Field[j].@name
				cpcFieldvalue = cpcFieldsXml.Field[j].@value
				if(tpcFieldID == cpcFieldID)
				{
					flag = 0
					if(tpcFieldname == cpcFieldname)
					{
						if(tpcFieldvalue != cpcFieldvalue)
						{
							logfile.append("For "+tpcTicker+", the value of field: "+tpcFieldID+", "+tpcFieldname+" doesnt match between TPC:"+tpcFieldvalue+" and CPC:"+cpcFieldvalue+"\n")
							log.error("For "+tpcTicker+": the value of field: "+tpcFieldID+", "+tpcFieldname+" doesnt match between TPC:"+tpcFieldvalue+" and CPC:"+cpcFieldvalue)
						}
					}
					else
					{
						logfile.append("For "+tpcTicker+", the fieldname for ID:"+tpcFieldID+",TPC field name: "+tpcFieldname+" doesnt match with CPC field name:"+cpcFieldname+"\n")
					}
					break
				}
				else
				{
					flag = 1
				}
			}
			if(flag == 1)
			{
				logfile.append("For "+tpcTicker+", Field ID:"+tpcFieldID+" with fieldname:"+tpcFieldname+" is not present in CPC\n")
				log.error("For "+tpcTicker+", Field ID:"+tpcFieldID+" with fieldname:"+tpcFieldname+" is not present in CPC\n")
			}
		}

		for(i=0; i<cpcFieldsXml.Field.size();i++)
		{
			cpcFieldID = cpcFieldsXml.Field[i].@id
			cpcFieldname = cpcFieldsXml.Field[i].@name
			cpcFieldvalue = cpcFieldsXml.Field[i].@value
			for(j=0;j<tpcFieldsXml.Field.size();j++)
			{
				tpcFieldID = tpcFieldsXml.Field[j].@id
				tpcFieldname = tpcFieldsXml.Field[j].@name
				tpcFieldvalue = tpcFieldsXml.Field[j].@value
				if(tpcFieldID == cpcFieldID)
				{
					flag = 0
					break
				}
				else
				{
					flag = 1
				}
			}
			if(flag == 1)
			{
				logfile.append("For "+cpcTicker+", Field ID:"+cpcFieldID+" with fieldname:"+cpcFieldname+" is not present in TPC\n")
				log.error("For "+cpcTicker+", Field ID:"+cpcFieldID+" with fieldname:"+cpcFieldname+" is not present in TPC\n")
			}
		}
	}
	else
	{
		logfile.append("For "+cpcTicker+", Data is not loaded in CPC\n")
		log.error("For "+cpcTicker+", Data is not loaded in CPC\n")
	}
}
else
{
	logfile.append("For "+tpcTicker+", Data is not loaded in TPC\n")
	log.error("For "+tpcTicker+", Data is not loaded in TPC\n")
}

import java.lang.AssertionError
