import groovy.xml.XmlUtil

def tpc = context.expand( '${Checking OC without any Filter#ResponseAsXml#//Response[1]/Records[1]}' )
def tpcXml = new XmlSlurper().parseText(tpc)

def id = context.expand( '${Checking OC without any Filter#OC}' )
def id2 = id.toString().split('-');

def option

assert tpcXml.Record.size() > 1 : "No Options were loaded for the ticker: "+id

for(i=0;i<tpcXml.Record.size();i++)
{
	option = tpcXml.Record[i].@req_sym
	option = option.toString()
	assert option.contains(id2[0].toUpperCase()) : "Option: "+option+" loaded for ticker: "+id+" is not valid";
}

import groovy.xml.XmlUtil

def id = context.expand( '${Checking OC with MINMAX Filter#OC}' )

def tpc = context.expand( '${Checking OC with MINMAX Filter#ResponseAsXml#//Response[1]/Records[1]}' )
def tpcXml = new XmlSlurper().parseText(tpc)

//log.info tpcXml.Record.size()
//log.info tpcXml.Record[0].Fields.Field.size()

assert tpcXml.Record.size() > 1 : "No Options were loaded for the ticker: "+id

for(i=0;i<tpcXml.Record.size();i++)
{
	for(j=0;j<tpcXml.Record[i].Fields.Field.size();j++)
	{
		def option = tpcXml.Record[i].@req_sym
		def fieldname = tpcXml.Record[i].Fields.Field[j].@name
		log.info fieldname
		if(fieldname == "BID_1")
		{
			def fieldvalue = tpcXml.Record[i].Fields.Field[j].@value
			def bidFloat = fieldvalue.toFloat()
			log.info bidFloat
			assert bidFloat >= 0.5 && bidFloat <= 0.65 : "For Option: "+option+", Bid Value is not in the range of 0.5 and 0.65"
		}
	}
}

import groovy.xml.XmlUtil

def id = context.expand( '${Checking OC with VALUE_EQUALS Filter#OC}' )

def tpc = context.expand( '${Checking OC with VALUE_EQUALS Filter#ResponseAsXml#//Response[1]/Records[1]}' )
def tpcXml = new XmlSlurper().parseText(tpc)
//log.info tpcXml.Record.size()
//log.info tpcXml.Record.Fields.Field.@name

assert tpcXml.Record.size() > 1 : "No Options were loaded for the ticker: "+id

for(i=0;i<tpcXml.Record.size();i++)
{
	for(j=0;j<tpcXml.Record[i].Fields.Field.size();j++)
	{
		def option = tpcXml.Record[i].@req_sym
		def fieldname = tpcXml.Record[i].Fields.Field[j].@name
		//log.info fieldname
		if(fieldname == "PUT_CALL")
		{
			def fieldvalue = tpcXml.Record[i].Fields.Field[j].@value
			assert fieldvalue == "P" : "For option: "+option+", PUT_CALL is not P"
		}
	}
}

import groovy.xml.XmlUtil

def tpc = context.expand( '${Checking OC with VALUE_NOT_EQUALS Filter#ResponseAsXml#//Response[1]/Records[1]}' )
def tpcXml = new XmlSlurper().parseText(tpc)
//log.info tpcXml.Record.size()
//log.info tpcXml.Record.Fields.Field.@name
def id = context.expand( '${Checking OC with VALUE_NOT_EQUALS Filter#OC}' )

assert tpcXml.Record.size() > 1 : "No Options were loaded for the ticker: "+id

for(i=0;i<tpcXml.Record.size();i++)
{
	for(j=0;j<tpcXml.Record[i].Fields.Field.size();j++)
	{
		def option = tpcXml.Record[i].@req_sym
		def fieldname = tpcXml.Record[i].Fields.Field[j].@name
		if(fieldname == "CUM_VOL")
		{
			def fieldvalue = tpcXml.Record[i].Fields.Field[j].@value
			//log.info fieldvalue.toInteger()
			assert fieldvalue != "0" : "For option: "+option+", Cum Vol is Zero"
		}
	}
}

